    private void transformarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_transformarActionPerformed
        // TODO add your handling code here:
        // paso 1 pedirle al textField su texto 
        String f= frase.getText();
        //debe ser arreglo de bits
        byte[]arreglo = f.getBytes();
        // creacion de objeto builder
        StringBuilder builder= new StringBuilder();
        
        // este ciclo hace el conversion por letra String builder 
        for(byte b:arreglo){
        builder.append(b);
        builder.append((','));
               
        }
        String t= builder.toString();
        jLabel1.setText(t);        
    }//GEN-LAST:event_transformarActionPerformed

