    private void retransformarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_retransformarActionPerformed
     // TODO add your handling code here:
        // paso 1 pedirle al textField su texto 
        String f= frase.getText();
        //debe ser arreglo de bits
        byte[]arreglo = f.getBytes();
        // creacion de objeto builder
        StringBuilder builder= new StringBuilder();
        
        // este ciclo hace el conversion por letra String builder 
        // para poder tomar la conversion de asqui del programita tenemos que hacer un casting (char()b)al arreglo for
        for(byte b:arreglo){
        builder.append((char)b);
        builder.append((','));
        }
        String t= builder.toString();
        jLabel2.setText(t);
    }//GEN-LAST:event_retransformarActionPerformed

